import csv
import re

def parse_and_convert_to_csv(input_text, output_filename="output.csv"):
    """
    Verilen metni ayrıştırır ve bir CSV dosyasına dönüştürür.
    
    Args:
        input_text (str): Ayrıştırılacak metin
        output_filename (str): Çıktı CSV dosyasının adı
    
    Returns:
        int: Ayrıştırılan kayıt sayısı, başarısızlık durumunda 0
    """
    if not input_text or not input_text.strip():
        print("Hata: Giriş metni boş veya geçersiz.")
        return 0
    
    # Her bir makaleyi ayıran regex deseni
    # Tablonun satırlarını yakalar: | | article_name | decision | reason | input_req_and_code |
    # Son iki sütun birleşik şekilde gelir ve daha sonra ayrıştırılır
    pattern = re.compile(
        r'\|\s*\|\s*([^|]+?)\s*\|\s*(KEEP|DISCARD|MAYBE)\s*\|\s*(.*?)\s*\|\s*(.*?)\s*\|',
        re.DOTALL
    )

    data = []
    # Metni tüm metin üzerinde arama yapalım
    matches = pattern.findall(input_text)
    
    if not matches:
        print("Metinden herhangi bir veri çıkarılamadı. Lütfen metin formatını kontrol edin.")
        print("Beklenen format: | | article_name | KEEP/DISCARD/MAYBE | reason | input_req_and_code |")
        return 0
    
    for match in matches:
        article_name = match[0].strip()
        decision = match[1].strip()
        reason = match[2].strip()
        combined_field = match[3].strip()  # This contains both input_req and code_eq
        
        # Skip empty records
        if not article_name:
            continue
        
        # Split the combined field if it contains "Kod/Denklik:"
        if "Kod/Denklik:" in combined_field:
            parts = combined_field.split("Kod/Denklik:", 1)
            input_req_clean = parts[0].strip()
            if input_req_clean.endswith("Girdi Gereksinimi:"):
                input_req_clean = input_req_clean[:-19].strip()  # Remove "Girdi Gereksinimi:" prefix
            if input_req_clean.startswith("Girdi Gereksinimi:"):
                input_req_clean = input_req_clean[19:].strip()  # Remove "Girdi Gereksinimi:" prefix
            code_eq_clean = parts[1].strip() if len(parts) > 1 else ""
        else:
            input_req_clean = combined_field
            if input_req_clean.startswith("Girdi Gereksinimi:"):
                input_req_clean = input_req_clean[19:].strip()
            code_eq_clean = ""
        
        data.append({
            'Makale Adı': article_name,
            'Karar': decision,
            'Neden': reason,
            'Girdi Gereksinimi': input_req_clean,
            'Kod/Denklik': code_eq_clean
        })

    if not data:
        print("Geçerli veri kaydı bulunamadı.")
        return 0

    try:
        # CSV dosyasına yazma
        with open(output_filename, 'w', newline='', encoding='utf-8') as csvfile:
            fieldnames = ['Makale Adı', 'Karar', 'Neden', 'Girdi Gereksinimi', 'Kod/Denklik']
            writer = csv.DictWriter(csvfile, fieldnames=fieldnames, delimiter=';')
            
            writer.writeheader()
            for row in data:
                writer.writerow(row)
        
        print(f"Başarı: {len(data)} kayıt başarıyla {output_filename} dosyasına yazıldı.")
        return len(data)
        
    except Exception as e:
        print(f"Hata: CSV dosyası yazılırken bir hata oluştu: {e}")
        return 0

